import Foundation //Question 3class Employee {    let name: String    let Salary: Int    let Technology: String    let Block : String        init (name: String, Salary: Int, Technology: String, Block: String) {        self.name = name        self.Salary = Salary        self.Technology = Technology        self.Block = Block    }}class MobileDev: Employee {    override init (name: String, Salary: Int, Technology: String, Block: String) {        super.init(name: name, Salary: Salary, Technology: Technology, Block: Block)    }    func bonuscalculate() {        print("The bonus of \(name) is \((Salary * 20)/100)")    }}class FrontDev: Employee {    override init (name: String, Salary: Int, Technology: String, Block: String) {        super.init(name: name, Salary: Salary, Technology: Technology, Block: Block)    }        func bonuscalculate() {        print("The bonus of \(name) is \((Salary * 18)/100)")    }}class BackDev: Employee {    override init (name: String, Salary: Int, Technology: String, Block: String) {        super.init(name: name, Salary: Salary, Technology: Technology, Block: Block)    }        func bonuscalculate() {        print("The bonus of \(name) is \((Salary * 15)/100)")    }}class QA: Employee {    override init (name: String, Salary: Int, Technology: String, Block: String) {        super.init(name: name, Salary: Salary, Technology: Technology, Block: Block)    }        func bonuscalculate() {        print("The bonus of \(name) is \((Salary * 22)/100)")    }}let employees: [Employee] = [    Employee(name: "Karan", Salary: 20000, Technology: "Mobile Development", Block: "A"),     Employee(name: "Aakash", Salary: 30000, Technology: "Front Development", Block: "B"),     Employee(name: "Suman", Salary: 30000, Technology: "Back Development", Block: "C"),     Employee(name: "Maanit", Salary: 15000, Technology: "QA", Block:"D"),    Employee(name: "Kunal", Salary: 30000, Technology: "Mobile Development", Block: "E"),    Employee(name: "Jayesh", Salary: 50000, Technology: "Back Development", Block: "C"),     Employee(name: "Isha", Salary: 25000, Technology: "QA", Block:"A"),]for employee in employees {    if(employee.Technology == "Mobile Development") {        let Mobileobj = MobileDev(        name: employee.name,         Salary: employee.Salary,         Technology: employee.Technology,         Block: employee.Block        )        Mobileobj.bonuscalculate()    }    if(employee.Technology == "Front Development") {        let Frontobj = FrontDev(        name: employee.name,         Salary: employee.Salary,         Technology: employee.Technology,         Block: employee.Block        )        Frontobj.bonuscalculate()    }    if(employee.Technology == "Back Development") {        let Backobj = BackDev(        name: employee.name,         Salary: employee.Salary,         Technology: employee.Technology,         Block: employee.Block        )        Backobj.bonuscalculate()    }    if(employee.Technology == "QA") {        let QAobj = QA(        name: employee.name,         Salary: employee.Salary,         Technology: employee.Technology,         Block: employee.Block        )        QAobj.bonuscalculate()    }}